tool.command:
if [[ \$(head -n 2 $infile | tail -n 1 | grep mzML) != "" ]] ; then 
    xcmsfile=input.mzML && 
    ln -s "$infile" \$xcmsfile && 
    save_chromatogram.r \$xcmsfile "$outfile" 2>&1;
else 
    echo "Error! Input file is not mzML or the header of the file is incomplete." && 
    exit 1;
fi;
