tool.command:

galaxy_data_dir="\$(dirname "${nmrMLzip}")";
archive_name="\$(basename "${nmrMLzip}")";
unzipped_path="\${galaxy_data_dir}/\${archive_name}_unzipped";
mkdir -p "\${unzipped_path}" && unzip -q -d "\${unzipped_path}" "${nmrMLzip}" && nmrML2batman.R -i "\${unzipped_path}";
exit_code=\$?;
rm -rf "\${unzipped_path}";
exit \${exit_code}
